import React, { Component } from "react";
import { Breadcrumb } from "@gull";
import LineChart4 from "app/views/charts/echarts/LineChart4";
import DoughnutChart from "app/views/charts/echarts/Doughnut";
import { Table } from "react-bootstrap";
import ReactPaginate from "react-paginate";
import SimpleCard from "@gull/components/cards/SimpleCard";
import { classList } from "@utils";
import {Link} from "react-router-dom";
import Chart from "react-apexcharts";

import {
  bAOptions18
} from "app/views/widgets/statisticsWidgetChartOptions";
import ReactEcharts from "echarts-for-react";
import {echartBasicDoughnutOption} from "../../charts/echarts/echartOptions";

class Project extends Component {
  state = {
    cardList: [
      {
        icon: "i-Add-User",
        title: "205",
        subtitle: "new leads"
      },
      {
        icon: "i-Financial",
        title: "4021",
        subtitle: "sales"
      },
      {
        icon: "i-Checkout-Basket",
        title: "80",
        subtitle: "checkout"
      },
      {
        icon: "i-Money-2",
        title: "120",
        subtitle: "expense"
      }
    ],
    topAuthorList: [
      {
        name: "David Hopkins",
        description: "Lorem ipsum dolor sit amet consectetur.",
        photoUrl: "/assets/images/faces/2.jpg"
      },
      {
        name: "James Mitchell",
        description: "Lorem ipsum dolor sit amet consectetur.",
        photoUrl: "/assets/images/faces/3.jpg"
      },
      {
        name: "Jessica Mitchell",
        description: "Lorem ipsum dolor sit amet consectetur.",
        photoUrl: "/assets/images/faces/4.jpg"
      }
    ],
    lastMonthSummery: [
      {
        name: "Portable Speaker",
        status: "+ $1200"
      },
      {
        name: "Portable Headphe",
        status: "In Stock"
      },
      {
        name: "Speaker",
        status: "Out of Stock"
      },
      {
        name: "Watch",
        status: "Low Stock"
      }
    ],
    newUserList: [
      {
        name: "David Hopkins",
        email: "hopkins@gmail.com",
        photoUrl: "/assets/images/faces/2.jpg",
        status: "active"
      },
      {
        name: "James Mitchell",
        email: "petter@gmail.com",
        photoUrl: "/assets/images/faces/3.jpg",
        status: "pending"
      },
      {
        name: "Jessica Mitchell",
        email: "johndoe@gmail.com",
        photoUrl: "/assets/images/faces/4.jpg",
        status: "inactive"
      }
    ],
    topProductList: [
      {
        name: "Watch",
        date: "12-10-2019",
        price: 30,
        status: "delivered"
      },
      {
        name: "Iphone",
        date: "24-10-2019",
        price: 350,
        status: "pending"
      },
      {
        name: "Headphone",
        date: "11-11-2019",
        price: 10,
        status: "not delivered"
      }
    ],
    chartList5: {
      title: "Item Pending",
      chart: bAOptions18
    }
  };

  handlePageClick = data => {
    let pageNumber = data.selected;
    let offset = Math.ceil(pageNumber * this.props.perPage);
    this.setState({ offset: offset }, () => {
      console.log(data);
    });
  };

  getUserStatusClass = status => {
    switch (status) {
      case "active":
        return "badge-success";
      case "delivered":
        return "badge-success";
      case "inactive":
        return "badge-warning";
      case "not delivered":
        return "badge-warning";
      case "pending":
        return "badge-primary";
      default:
        break;
    }
  };

  getStatusTextColor = status => {
    switch (status) {
      case "In Stock":
        return "text-success";
      case "Low Stock":
        return "text-warning";
      case "Out of Stock":
        return "text-danger";
      default:
        return "text-primary";
    }
  };

  render() {
    let {
      cardList = [],
      lastMonthSummery = [],
      topAuthorList = [],
      newUserList = [],
      topProductList = [],
      chartList5 = { }
    } = this.state;

    return (
      <div>
        <Breadcrumb
          routeSegments={[
            { name: "Project Dashboard" }
          ]}
        ></Breadcrumb>

        <div className="card mb-4">
          <div className="card-header">Featured</div>
          <div className="card-body">
            <h5 className="card-title">Project title text</h5>
            <div className="row">
              <div className="col-md-6">
                <p className="card-text">
                  With supporting text below as a natural lead-in to
                  additional content.
                </p>
              </div>
            </div>
            <div className="row">
              {cardList.map((card, index) => (
                  <div key={index} className="col-lg-3 col-md-6 col-sm-6">
                    <div className="card card-icon-bg card-icon-bg-primary o-hidden mb-4">
                      <div className="card-body text-center">
                        <i className={card.icon}></i>
                        <div className="content">
                          <p className="text-muted mt-2 mb-0 text-capitalize">
                            {card.subtitle}
                          </p>
                          <p className="lead text-primary text-24 mb-2 text-capitalize">
                            {card.title}
                          </p>
                        </div>
                      </div>
                    </div>
                  </div>
              ))}

              <div className="col-lg-3 col-md-6 col-sm-6">
                <div className="card card-icon-bg card-icon-bg-primary o-hidden mb-4">
                  <div className="card-body text-center">
                    <i className={card.icon}></i>
                    <div className="content">
                      <p className="text-muted mt-2 mb-0 text-capitalize">
                        {card.subtitle}
                      </p>
                      <p className="lead text-primary text-24 mb-2 text-capitalize">
                        {card.title}
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <Link to="/" className="btn btn-primary btn-rounded">
              Go somewhere
            </Link>
          </div>
        </div>

        <div className="card mb-4">
          <div className="card-header">Featured</div>
          <div className="card-body">
            <h5 className="card-title">Card title text</h5>
            <p className="card-text">
              With supporting text below as a natural lead-in to
              additional content.
            </p>
            <div className="col-md-6">
              <DoughnutChart height="200px"></DoughnutChart>
            </div>
            <Link to="/" className="btn btn-primary btn-rounded">
              Go somewhere
            </Link>
          </div>
        </div>


      </div>
    );
  }
}

export default Project;
